import React, { ChangeEvent, Children, FunctionComponent, useEffect, useRef, useState } from "react";
import AppHeader from "../../accessories/appHeader/AppHeader";
import Footer from "../../accessories/footer/Footer";
import Button from "@material-ui/core/Button";
import Table from '@material-ui/core/Table';
import TextData from '@material-ui/core/TextField'
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { useTranslation } from "react-i18next";
import { connect } from "react-redux";
import { IState } from "../../../types";
import "./styles.scss";
import HighlightOffIcon from '@material-ui/icons/HighlightOff';
import {
    createBill,
    createBillReset,
    getBill,
} from "../../../state/bills/actions";
import {
    getPrices,
    getPriceLists
} from "../../../state/prices/actions"
import {
    getMedicals,
} from "../../../state/medicals/actions"
import {
    IDispatchProps,
    IStateProps,
    TProps,
    Item,
} from "./types";
import DrawerActivity from "./DrawerActivity";


const NewBillActivity: FunctionComponent<TProps> = ({
    userCredentials,
    createBill,
    createBillReset,
    billHomeRoute,
    med,
    getMedicals,
    getMedStat,
    bill,
    getBill,
    prices,
    getPrices,
    priceLists,
    getPriceLists,
}) => {

    const { t } = useTranslation();
    type Custom = {
        id: number,
        description: string,
        amount: string,
    }

    const breadcrumbMap = {
        [t("nav.dashboard")]: "/",
        [t("nav.billing")]: "/billing",
        [t("nav.newbill")]: "/bills",
    };
    useEffect(() => {
        getMedicals();
    }, []);

    useEffect(() => {
        getPrices();
    }, []);

    //  //  //  //  //
    // GET PATIENT  //
    //  //  //  //  //
    type Patient = {
        "code": number | undefined,
        "firstName": string | undefined,
        "secondName": string | undefined,
        "name": string | undefined,
        "birthDate": string | undefined,
        "age": number | undefined,
        "agetype": string | undefined,
        "sex": string | undefined,
        "address": string | undefined,
        "city": string | undefined,
        "nextKin": string | undefined,
        "telephone": string | undefined,
        "note": string | undefined,
        "mother_name": string | undefined,
        "mother": string | undefined,
        "father_name": string | undefined,
        "father": string | undefined,
        "bloodType": string | undefined,
        "hasInsurance": string | undefined,
        "parentTogether": string | undefined,
        "taxCode": string | undefined,
        "lock": number | undefined,
        "blobPhoto": string | undefined,
        "hashCode": number | undefined,
    }


    const [patient, setPatient] = useState<Patient>();

    useEffect(() => {
        const url = "https://www.open-hospital.org/oh11-api/patients?page=1&size=1";
        const auth = "Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJhZG1pbiIsImF1dGgiOiJhZG1pbiIsImV4cCI6MTYyOTc1ODQ4NH0.BLAxHfBxyOJ3CI9gnfGbm7UshTVUzZvEA8WFrqh_ZN4jHCDB05d4Ypy1rVML19_JLsXf-kNUTA5G0L7Ts0V_lQ";
        const acc = "application/json";

        const fetchData = async () => {
            try {
                const response = await fetch(url, { method: 'GET', headers: { Accept: acc, Authorization: auth } });
                const json = await response.json();
                setPatient(json[0])

            } catch (error) {
                console.log("error", error);
            }
        };
        fetchData();
    }, []);



    const patient_data = patient?.firstName + " " + patient?.secondName;



    const delete_item = (e: Item) => {
        setItems(items.filter(item => e !== item));
    }
    const [items, setItems] = React.useState<Item[]>([]);

    const test = () => {
        console.log(Children.count)
    }

    return (
        <div className="new_Bill">
            <AppHeader
                userCredentials={userCredentials}
                breadcrumbMap={breadcrumbMap}
            />
            <div className="newBill__background">
                <div className="newBill__content">
                    <div className="newBill__title">{t("nav.newbill")}</div>
                    <div className="newBill__panel">
                        <form>
                            <div className="newBill_Head">
                                <div className="newBill_Date_Pat">
                                    <TextData
                                        className='bill_Date'
                                        id="date"
                                        label="Data"
                                        type="date"
                                        defaultValue=""
                                        InputLabelProps={{
                                            shrink: true,
                                        }}
                                    />
                                    <Button type="submit" className='bill_SelectPat' onClick={test} variant="outlined">find Patient</Button>
                                </div>
                                <div className="newBill_InputPat"><label>Patient</label><span></span><input className="patient_input" disabled ></input></div>
                            </div>
                            <div className="newBill_Rows">
                                <TableContainer component={Paper}>
                                    <Table size="small" aria-label="a dense table">
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>Description</TableCell>
                                                <TableCell>Amount</TableCell>
                                                <TableCell>Qty</TableCell>
                                                <TableCell>Delete</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {items?.map((x, y) => {
                                                return (
                                                    <TableRow>
                                                        <TableCell>{x.hashCode}</TableCell>
                                                        <TableCell>{x.itemAmount}</TableCell>
                                                        <TableCell>{x.itemQuantity}</TableCell>
                                                        <TableCell><Button onClick={() => delete_item(x)}><HighlightOffIcon></HighlightOffIcon></Button></TableCell>
                                                    </TableRow>
                                                )
                                            }
                                            )}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                            </div>
                        </form>
                        <div className="newBill_Drawer">
                            <DrawerActivity prices={prices} items={items} setItems={setItems} />
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}


const mapStateToProps = (state: IState): IStateProps => ({
    userCredentials: state.main.authentication.data,
    isLoading: state.bills.createBill.status === "LOADING",
    hasSucceeded: state.bills.createBill.status === "SUCCESS",
    hasFailed: state.bills.createBill.status === "FAIL",
    bill: state.bills.getBill.data,
    med: state.medicals.medicalsOrderByName.data,
    prices: state.prices.getPrices.data,
    getMedStat: state.medicals.medicalsOrderByName.status || "IDLE",
    priceLists: state.prices.getPriceLists.data
});




const mapDispatchToProps: IDispatchProps = {
    createBill,
    createBillReset,
    getMedicals,
    getBill,
    getPrices,
    getPriceLists
};

export default connect(mapStateToProps, mapDispatchToProps)(NewBillActivity);
