import { Dispatch } from "redux";
import {
    Configuration,
    BillControllerApi,
    BillDTO,
    FullBillDTO,
} from "../../generated";
import { TValues } from "../../components/activities/searchBillActivity/types";
import { applyTokenMiddleware } from "../../libraries/apiUtils/applyTokenMiddleware";
import { IAction } from "../types";
import {
    CREATE_BILL_FAIL,
    CREATE_BILL_LOADING,
    CREATE_BILL_RESET,
    CREATE_BILL_SUCCESS,
    SEARCH_BILL_FAIL,
    SEARCH_BILL_LOADING,
    SEARCH_BILL_SUCCESS,
} from "./consts";

const billControllerApi = new BillControllerApi(
    new Configuration({ middleware: [applyTokenMiddleware] })
);

export const createBill =
    () =>
        (dispatch: Dispatch<IAction<null, {}>>): void => {
            dispatch({
                type: CREATE_BILL_LOADING,
                ciao: 1,
            });
        };

export const createBillReset =
    () =>
        (dispatch: Dispatch<IAction<null, {}>>): void => {
            dispatch({
                type: CREATE_BILL_RESET,
            });
        };

export const searchPatient =
    (values: TValues) =>
        (dispatch: Dispatch<IAction<BillDTO[], {}>>): void => {
            dispatch({
                type: SEARCH_PATIENT_LOADING,
            });

            if (values.id) {
                patientControllerApi
                    .getPatientUsingGET({ code: parseInt(values.id) })
                    .subscribe(
                        (payload) => {
                            if (typeof payload === "object" && !isEmpty(payload)) {
                                dispatch({
                                    type: SEARCH_PATIENT_SUCCESS,
                                    payload: [payload],
                                });
                            } else {
                                dispatch({
                                    type: SEARCH_PATIENT_SUCCESS,
                                    payload: [],
                                });
                            }
                        },
                        (error) => {
                            dispatch({
                                type: SEARCH_PATIENT_FAIL,
                                error,
                            });
                        }
                    );
            } else {
                patientControllerApi.searchPatientUsingGET(values).subscribe(
                    (payload) => {
                        if (Array.isArray(payload)) {
                            dispatch({
                                type: SEARCH_PATIENT_SUCCESS,
                                payload,
                            });
                        } else {
                            dispatch({
                                type: SEARCH_PATIENT_FAIL,
                                error: { message: "Unexpected response payload" },
                            });
                        }
                    },
                    (error) => {
                        dispatch({
                            type: SEARCH_PATIENT_FAIL,
                            error,
                        });
                    }
                );
            }
        };

export const getPatientSuccess = (
    patient: PatientDTO
): IAction<PatientDTO, {}> => {
    return {
        type: GET_PATIENT_SUCCESS,
        payload: patient,
    };
};
