import React, { FunctionComponent, useEffect, useRef, useState } from "react";
import AppHeader from "../../accessories/appHeader/AppHeader";
import Footer from "../../accessories/footer/Footer";
import Button from "@material-ui/core/Button";
import Modal from "@material-ui/core/Modal";
import { useTranslation } from "react-i18next";
import { connect, useDispatch } from "react-redux";
import { Redirect } from "react-router";
import { IState } from "../../../types";
import { useFormik } from "formik";
import TextField from "../../accessories/textField/TextField";
import { BillDTO, PatientDTO } from "../../../generated";
import { FullBillDTO } from "../../../generated";
import { scrollToElement } from "../../../libraries/uiUtils/scrollToElement";
import Drawer from '@material-ui/core/Drawer';
import AppBar from '@material-ui/core/AppBar';
import "./styles.scss";
import SearchIcon from "../../../assets/SearchIcon";

import {
    createBill,
    createBillReset,
} from "../../../state/bills/actions";

import {
    searchPatient,
    getPatientSuccess,
} from "../../../state/patients/actions";

import {
    IDispatchProps,
    IStateProps,
    TActivityTransitionState,
    TProps,
    TValues,
} from "./types";
import patientDTO from "../../../mockServer/fixtures/patientDTO";


const NewBillActivity: FunctionComponent<TProps> = ({
    userCredentials,
    isLoading,
    hasSucceeded,
    getMed,
    createBill,
    createBillReset,
    hasFailed,
    billHomeRoute,
}) => {
    const { t } = useTranslation();

    const breadcrumbMap = {
        [t("nav.dashboard")]: "/",
        [t("nav.billing")]: "/billing",
        [t("nav.newbill")]: "/bills",
    };
    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };


    const body = (
        <div>
            <p>ciao</p>
        </div>
    )



    return (
        <div className="new_Bill">
            <AppHeader
                userCredentials={userCredentials}
                breadcrumbMap={breadcrumbMap}
            />
            <div className="newBill__background">
                <div className="newBill__content">
                    <div className="newBill__title">{t("nav.newbill")}</div>
                    <div className="newBill__panel">
                        <div className="table">
                            <label>test inserimento</label>
                            <button type="button" onClick={handleOpen}>
                                Open Modal
                            </button>
                            <Modal
                                open={open}
                                onClose={handleClose}
                                aria-labelledby="simple-modal-title"
                                aria-describedby="simple-modal-description">
                                {body}
                            </Modal>
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}


const mapStateToProps = (state: IState): IStateProps => ({
    userCredentials: state.main.authentication.data,
    isLoading: state.bills.createBill.status === "LOADING",
    hasSucceeded: state.bills.createBill.status === "SUCCESS",
    hasFailed: state.bills.createBill.status === "FAIL",
    getMed: state.medicals.medicalsOrderByName.data,
});




const mapDispatchToProps: IDispatchProps = {
    createBill,
    createBillReset,

};

export default connect(mapStateToProps, mapDispatchToProps)(NewBillActivity);
