import React, { FunctionComponent, useEffect, useRef, useState } from "react";
import AppHeader from "../../accessories/appHeader/AppHeader";
import Footer from "../../accessories/footer/Footer";
import Button from "@material-ui/core/Button";
import Modal from "@material-ui/core/Modal";
import patientDTO from "/home/zak/Tesi/OH_React_Tesi/src/mockServer/fixtures/patientDTO.js";
import Table from '@material-ui/core/Table';
import TextData from '@material-ui/core/TextField'
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import List from '@material-ui/core/List';
import Paper from '@material-ui/core/Paper';
import { green } from '@material-ui/core/colors';
import Icon from '@material-ui/core/Icon';
import { useTranslation } from "react-i18next";
import { connect, useDispatch } from "react-redux";
import { Redirect } from "react-router";
import { IState } from "../../../types";
import { useFormik } from "formik";
import TextField from "../../accessories/textField/TextField";
import { BillDTO, PatientDTO } from "../../../generated";
import { FullBillDTO } from "../../../generated";
import { scrollToElement } from "../../../libraries/uiUtils/scrollToElement";
import Drawer from '@material-ui/core/Drawer';
import AppBar from '@material-ui/core/AppBar';
import "./styles.scss";
import SearchIcon from "../../../assets/SearchIcon";

import {
    createBill,
    createBillReset,
} from "../../../state/bills/actions";

import {
    getMedicals,
} from "../../../state/medicals/actions"

import { MedicalDTO } from "../../../generated";

import {
    IDispatchProps,
    IStateProps,
    TActivityTransitionState,
    TProps,
    TValues,
} from "./types";

import { getPatientSuccess } from "/home/zak/Tesi/OH_React_Tesi/src/state/patients/actions";



const NewBillActivity: FunctionComponent<TProps> = ({
    userCredentials,
    isLoading,
    hasSucceeded,
    createBill,
    createBillReset,
    hasFailed,
    billHomeRoute,
    med,
    getMedicals,
    getMedStat,
    patient,
    patientSearchResults,
    getPatientSuccess
}) => {
    const { t } = useTranslation();

    const breadcrumbMap = {
        [t("nav.dashboard")]: "/",
        [t("nav.billing")]: "/billing",
        [t("nav.newbill")]: "/bills",
    };

    //
    //Gestione Modal
    //
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };
    const body = (
        <div>
            <p>ciao</p>
        </div>
    )
    //
    //
    const x = med;

    useEffect(() => {
        getPatientSuccess(patient);
    }, []);

    const tet = () => {
        patient?.map((p, i) => {
            console.log(i,p.firstName);
        })

    }


    return (
        <div className="new_Bill">
            <AppHeader
                userCredentials={userCredentials}
                breadcrumbMap={breadcrumbMap}
            />
            <div className="newBill__background">
                <div className="newBill__content">
                    <div className="newBill__title">{t("nav.newbill")}</div>
                    <div className="newBill__panel">
                        <div className="table">
                            <div className="container">
                                <form>
                                    <div className="bill_DatePat">
                                        <TextData
                                            className='bill_Date'
                                            id="date"
                                            label="Data"
                                            type="date"
                                            defaultValue=""
                                            InputLabelProps={{
                                                shrink: true,
                                            }}
                                        />
                                        <Button className='bill_SelectPat' variant="outlined">Paziente</Button>
                                    </div>
                                </form>
                            </div>
                            <div className="bill_Insert">
                                <Divider />
                                <p>test</p>
                                <div className="test">
                                    <Button onClick={tet}>click</Button>
                                </div>
                                <TableContainer component={Paper} className='bill_Table'>
                                    <Table size="small" aria-label="a dense table">
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>Total</TableCell>
                                                <TableCell align="left"></TableCell>
                                                <TableCell align="left"></TableCell>
                                                <TableCell align="left"></TableCell>
                                                <TableCell align="left">0</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {med?.map((m,i) => (
                                                <TableRow>
                                                    <TableCell>{i}</TableCell>
                                                    <TableCell>{m.description}</TableCell>
                                                </TableRow>
                                            ))}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                                <List className="bill_Drawer">
                                    {['Medical', 'Operation', 'Exam ', 'Other'].map((text, index) => (
                                        <ListItem button key={text}>
                                            <Icon className="fa fa-plus-circle" style={{ color: green[500] }} />
                                            <ListItemText primary={text} />
                                        </ListItem>
                                    ))}
                                </List>
                                <Divider />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}


const mapStateToProps = (state: IState): IStateProps => ({
    userCredentials: state.main.authentication.data,
    isLoading: state.bills.createBill.status === "LOADING",
    hasSucceeded: state.bills.createBill.status === "SUCCESS",
    hasFailed: state.bills.createBill.status === "FAIL",
    med: state.medicals.medicalsOrderByName.data,
    getMedStat: state.medicals.medicalsOrderByName.status || "IDLE"
});




const mapDispatchToProps: IDispatchProps = {
    createBill,
    createBillReset,
    getMedicals,
};

export default connect(mapStateToProps, mapDispatchToProps)(NewBillActivity);
