import Select from '@material-ui/core/Select';
import { PatientDTO } from "../../../generated";
import {
    createBill,
    createBillReset,
    getBill,
} from "../../../state/bills/actions";
import {
    getPrices,
    getPriceLists
} from "../../../state/prices/actions"
import {
    getMedicals,
} from "../../../state/medicals/actions"
import {
    IDispatchProps,
    IStateProps,
    TProps,
} from "./types";
import patientDTO from "../../../mockServer/fixtures/patientDTO";


const NewBillActivity: FunctionComponent<TProps> = ({
    userCredentials,
    isLoading,
    hasSucceeded,
    createBill,
    createBillReset,
    hasFailed,
    billHomeRoute,
    med,
    getMedicals,
    getMedStat,
    bill,
    getBill,
    prices,
    getPrices,
    priceLists,
    getPriceLists
}) => {
    const { t } = useTranslation();
    type Custom = {
        id: number,
        description: string,
        amount: string,
    }

    const breadcrumbMap = {
        [t("nav.dashboard")]: "/",
        [t("nav.billing")]: "/billing",
        [t("nav.newbill")]: "/bills",
    };
    useEffect(() => {
        getMedicals();
    }, []);

    useEffect(() => {
        getPrices();
    }, []);


    //
    //Gestione Dialog
    //
    const [open_med, setOpen_med] = React.useState(false);

    const handleClickOpen_med = () => {
        setOpen_med(true);
    };

    const handleClose_med = () => {
        setOpen_med(false);
    };

    const [open_ope, setOpen_ope] = React.useState(false);

    const handleClickOpen_ope = () => {
        setOpen_ope(true);
    };

    const handleClose_ope = () => {
        setOpen_ope(false);
    };

    const [open_exa, setOpen_exa] = React.useState(false);

    const handleClickOpen_exa = () => {
        setOpen_exa(true);
    };

    const handleClose_exa = () => {
        setOpen_exa(false);
    };

    const [open_oth, setOpen_oth] = React.useState(false);

    const handleClickOpen_oth = () => {
        setOpen_oth(true);
    };

    const handleClose_oth = () => {
        setOpen_oth(false);
    };

    const [open_cust, setOpen_cust] = React.useState(false);

    const handleClickOpen_cust = () => {
        setOpen_cust(true);
    };

    const handleClose_cust = () => {
        setOpen_cust(false);
    };
    //
    //

    //
    //test useState
    //
    type Item = {
        id?: number;
        description?: string;
        amount?: number;
        item_quant?: number;
    }

    const [selectionModel, setSelectionModel] = React.useState<GridSelectionModel>([]);

    const [items, setItems] = React.useState<Item[]>([]);
    const [medicals, setMedicals] = React.useState<GridSelectionModel>([]);
    const [operations, setOperations] = React.useState<GridSelectionModel>([]);
    const [exams, setExams] = React.useState<GridSelectionModel>([]);
    const [others, setOthers] = React.useState<GridSelectionModel>([]);
    const [custom, setCustom] = React.useState<Custom>();

    const take_items = () => {

        medicals?.forEach(function (i, j) {
            if ((items.some(it => it.id === i)) === true) {
                items?.forEach(function (x) {
                    if (x.id === i)
                        console.log('ciao');
                })
            }
            else {
                //item.push({ id: prices?.find(p => p.id == i)?.id, description: prices?.find(p => p.id == i)?.description, amount: prices?.find(p => p.id == i)?.price, item_quant: 1 });
                setItems(pre =>
                    [...pre,
                    {
                        id: prices?.find(p => p.id == i)?.id,
                        description: prices?.find(p => p.id == i)?.list?.description,
                        amount: prices?.find(p => p.id == i)?.price,
                        item_quant: 1
                    }]);
            }
        })
    }
    /*
    const get_items_o = () => {
        operations?.forEach(function (i, j) {
            if (!(items.includes(i)))
                setItems(prevItems => [...prevItems, i]);
        })
    }
    const get_items_e = () => {
        exams?.forEach(function (i, j) {
            if (!(items.includes(i)))
                setItems(prevItems => [...prevItems, i]);
        })
    }
    const get_items_ot = () => {
        others?.forEach(function (i, j) {
            if (!(items.includes(i)))
                setItems(prevItems => [...prevItems, i]);
        })
    }

    const get_items_cust = () => {
        const i: GridRowId = 2;
        setItems(prevItems => [...prevItems, i]);
    }

    const delete_item = (e: GridRowId) => {
        console.log(e);
        setItems(items.filter(item => e !== item));
    }


*/
    const get_input = (e: ChangeEvent<HTMLTextAreaElement | HTMLInputElement>) => {
        const a = e.target.id as string;
        console.log(a);

        //setCustom({id:1, description:'ciao'});
    }

    const get_cust = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        const desc = (e.currentTarget.elements[0] as HTMLInputElement).value as string;
        const amo = (e.currentTarget.elements[1] as HTMLInputElement).value as string;
        setCustom({ id: 1, description: desc, amount: amo });
        console.log(custom);

    }

    const test = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        console.log((e.currentTarget.elements[4] as HTMLInputElement)?.value);
    }
    //
    //

    //  //  //  //  //  //
    //  gestione select //
    //  //  //  //  //  //
    const [quantity, setQuantity] = React.useState('1');
    const handleChangeSelect = (event: React.ChangeEvent<{ value: unknown }>) => {
        setQuantity(event.target.value as string);
    };


    //  //  //  //  //  //
    // Gestione Grid MED//
    //  //  //  //  //  //
    const columns_med = [
        { field: 'id', headerName: 'ID', width: 90 },
        {
            field: 'group',
            headerName: 'Group',
            width: 125,
            editable: true,
        },
        {
            field: 'description',
            headerName: 'Description',
            width: 170,
            editable: true,
        },
    ];

    type row = [{
        id: number | undefined,
        group: string,
        description: string | undefined
    }];

    let rows_med: row = [{ id: 0, group: '', description: '' }];

    prices?.forEach(function (m, i) {
        if (m.group === "MED")
            rows_med.push({ id: m.id, group: m.group, description: m.list?.description })
    })
    rows_med?.shift();

    //  //  //  //  //  //
    //  GESTIONE ROWS   //
    //  //  //  //  //  //
    const [row, setRow] = useState('');
    //
    //

    //  //  //  //  //  //
    // Gestione Grid OPE//
    //  //  //  //  //  //
    const columns_ope: GridColDef[] = [
        { field: 'id', headerName: 'ID', width: 90 },
        {
            field: 'group',
            headerName: 'Group',
            width: 125,
            editable: true,
        },
        {
            field: 'description',
            headerName: 'Description',
            width: 170,
            editable: true,
        },
    ];

    const rows_ope = [{}];

    prices?.forEach(function (m, i) {
        if (m.group === "OPE")
            rows_ope.push({ id: m.id, group: m.group, description: m.list?.description })
    })
    rows_ope.shift();
    //
    //

    //  //  //  //  //  //
    // Gestione Grid EXA//
    //  //  //  //  //  //
    const columns_exa: GridColDef[] = [
        { field: 'id', headerName: 'ID', width: 90 },
        {
            field: 'group',
            headerName: 'Group',
            width: 125,
            editable: true,
        },
        {
            field: 'description',
            headerName: 'Description',
            width: 170,
            editable: true,
        },
    ];

    const rows_exa = [{}];

    prices?.forEach(function (m, i) {
        if (m.group === "EXA")
            rows_exa.push({ id: m.id, group: m.group, description: m.list?.description })
    })
    rows_exa.shift();
    //
    //

    //  //  //  //  //  //
    // Gestione Grid OTH//
    //  //  //  //  //  //
    const columns_oth: GridColDef[] = [
        { field: 'id', headerName: 'ID', width: 90 },
        {
            field: 'group',
            headerName: 'Group',
            width: 125,
            editable: true,
        },
        {
            field: 'description',
            headerName: 'Description',
            width: 170,
            editable: true,
        },
    ];

    const rows_oth = [{}];

    prices?.forEach(function (m, i) {
        if (m.group === "OTH")
            rows_oth.push({ id: m.id, group: m.group, description: m.list?.description })
    })
    rows_oth.shift();
    //
    //
    type Patient = {
        "code": number | undefined,
        "firstName": string | undefined,
        "secondName": string | undefined,
        "name": string | undefined,
        "birthDate": string | undefined,
        "age": number | undefined,
        "agetype": string | undefined,
        "sex": string | undefined,
        "address": string | undefined,
        "city": string | undefined,
        "nextKin": string | undefined,
        "telephone": string | undefined,
        "note": string | undefined,
        "mother_name": string | undefined,
        "mother": string | undefined,
        "father_name": string | undefined,
        "father": string | undefined,
        "bloodType": string | undefined,
        "hasInsurance": string | undefined,
        "parentTogether": string | undefined,
        "taxCode": string | undefined,
        "lock": number | undefined,
        "blobPhoto": string | undefined,
        "hashCode": number | undefined,
    }

    const [patient, setPatient] = useState<Patient>();


    //fetch('https://www.open-hospital.org/oh11-api/pricelists/prices').then(res => res.json()).then(data => console.log(data)).catch(error => console.log('male'));
