import React, { FunctionComponent, useEffect, useRef, useState } from "react";
import AppHeader from "../../accessories/appHeader/AppHeader";
import Footer from "../../accessories/footer/Footer";
import Button from "@material-ui/core/Button";
import Table from '@material-ui/core/Table';
import TextData from '@material-ui/core/TextField'
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Paper from '@material-ui/core/Paper';
import { useTranslation } from "react-i18next";
import { connect, useDispatch } from "react-redux";
import { IState } from "../../../types";
import "./styles.scss";
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import { DataGrid, GridColDef, GridValueGetterParams, GridSelectionModel, GridRowId } from '@material-ui/data-grid';
import { useDemoData } from '@material-ui/x-grid-data-generator';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import AddIcon from '@material-ui/icons/Add';
import SaveIcon from '@material-ui/icons/Save';
import {
    createBill,
    createBillReset,
    getBill,
} from "../../../state/bills/actions";
import {
    getPrices,
    getPriceLists
} from "../../../state/prices/actions"
import {
    getMedicals,
} from "../../../state/medicals/actions"
import {
    IDispatchProps,
    IStateProps,
    TProps,
} from "./types";


const NewBillActivity: FunctionComponent<TProps> = ({
    userCredentials,
    isLoading,
    hasSucceeded,
    createBill,
    createBillReset,
    hasFailed,
    billHomeRoute,
    med,
    getMedicals,
    getMedStat,
    bill,
    getBill,
    prices,
    getPrices,
    priceLists,
    getPriceLists
}) => {
    const { t } = useTranslation();

    const breadcrumbMap = {
        [t("nav.dashboard")]: "/",
        [t("nav.billing")]: "/billing",
        [t("nav.newbill")]: "/bills",
    };
    useEffect(() => {
        getMedicals();
    }, []);

    useEffect(() => {
        getPrices();
    }, [])
    //
    //Gestione Dialog
    //
    const [open, setOpen] = React.useState(false);

    const handleClickOpen_med = () => {
        setOpen(true);
    };

    const handleClickOpen_ope = () => {
        setOpen(true);
    };

    const handleClickOpen_exa = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };
    //
    //

    //
    //test useState
    //

    const [medicals, setmedicals] = React.useState<GridSelectionModel>([]);
    let item: GridRowId = '';

    const add_med = () => {
        medicals?.forEach(function (m, i) {
            item = m;
        })
    }
    const get_i = () => {
        return (
            <p>{item}</p>
        )
    }
    //
    //


    //  //  //  //  //  //
    // Gestione Grid MED//
    //  //  //  //  //  //
    const columns_med: GridColDef[] = [
        { field: 'id', headerName: 'ID', width: 90 },
        {
            field: 'group',
            headerName: 'Group',
