import React, { FunctionComponent, useEffect, useRef, useState } from "react";
import AppHeader from "../../accessories/appHeader/AppHeader";
import Footer from "../../accessories/footer/Footer";
import Button from "@material-ui/core/Button";
import { useTranslation } from "react-i18next";
import { connect } from "react-redux";
import { Redirect } from "react-router";
import { IState } from "../../../types";
import { useFormik } from "formik";
import { Divider, TextField } from '@material-ui/core';
import { BillDTO } from "../../../generated";
import { FullBillDTO } from "../../../generated";
import { scrollToElement } from "../../../libraries/uiUtils/scrollToElement";
import Drawer from '@material-ui/core/Drawer';
import AppBar from '@material-ui/core/AppBar';
import "./styles.scss";
import SearchIcon from "../../../assets/SearchIcon";
import Test from "./Test";
import {
    createBill,
    createBillReset,
} from "../../../state/bills/actions";
import {
    searchPatient,
    getPatientSuccess,
} from "../../../state/patients/actions";

import {
    IDispatchProps,
    IStateProps,
    TActivityTransitionState,
    TProps,
    TValues,
} from "./types";


const NewBillActivity: FunctionComponent<TProps> = ({
    userCredentials,
    isLoading,
    hasSucceeded,
    createBill,
    createBillReset,
    hasFailed,
    billHomeRoute,
    patientSearchResults,
    getPatientSuccess,
    searchStatus,
}) => {
    const { t } = useTranslation();

    const breadcrumbMap = {
        [t("nav.dashboard")]: "/",
        [t("nav.billing")]: "/billing",
        [t("nav.newbill")]: "/bills",
    };

    const isValid = (fieldName: string): boolean => {
        return has(formik.touched, fieldName) && has(formik.errors, fieldName);
      };

      const getErrorText = (fieldName: string): string => {
        return has(formik.touched, fieldName) ? get(formik.errors, fieldName) : "";
      };

    const [activityTransitionState, setActivityTransitionState] =
        useState<TActivityTransitionState>("IDLE");

    const test_succ = "SEARCH_PATIENT_SUCCESS";

    const initialValues: TValues = {
        id: "",
        firstName: "",
        secondName: "",
        birthDate: "",
        address: "",
    };

    const formik = useFormik({
        initialValues,
        onSubmit: (values: TValues) => {
            // First scroll to show searching message

            searchPatient(values);
        },
    });

    const resultsRef = useRef<HTMLDivElement>(null);
    /*useEffect(() => {
        if (
            activityTransitionState === "TO_NEW_BILL_RESET" ||
            activityTransitionState === "TO_BILL_HOME"
        ) {
            createBillReset();
            setShouldResetForm(true);
        }
    }, [activityTransitionState, createBillReset]);


    //const infoBoxRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        if (hasFailed) {
            scrollToElement(infoBoxRef.current);
        }
    }, [hasFailed]);

    const [shouldResetForm, setShouldResetForm] = useState(false);

    const resetFormCallback = () => {
        setShouldResetForm(false);
        setActivityTransitionState("IDLE");
        scrollToElement(null);
    };

    const formik = useFormik({
        initialValues: {
            firstName: '',
        },
        onSubmit: (values) => {
            console.log(values);
        },
    });
    */
    const renderSearchResults = (): JSX.Element | undefined => {
        switch (searchStatus) {
            case "IDLE":
                return;

            case "LOADING":
                return (
                    <h3 className="searchPatient__loading">{t("common.searching")}</h3>
                );

            case "SUCCESS":
                return (
                    <div className="searchPatient__results">
                        <div className="searchPatient__results_count">
                            {t("common.results")}:{" "}
                            <strong>{patientSearchResults?.length}</strong>
                        </div>
                        <div className="searchPatient__results_list">
                            {patientSearchResults?.map((patient, index) => (
                                <Test
                                    key={index}
                                    patient={patient}
                                    getPatientSuccessCallback={getPatientSuccess}
                                />
                            ))}
                        </div>
                    </div>
                );
        }
    };

    return (
        <div className="new_Bill">
            <AppHeader
                userCredentials={userCredentials}
                breadcrumbMap={breadcrumbMap}
            />
            <div className="newBill__background">
                <div className="newBill__content">
                    <div className="newBill__title">{t("nav.newbill")}</div>
                    <div className="newBill__panel">
                        <div className="table">
                            <form onSubmit={formik.handleSubmit}>
                                <div className="searchPatient__primary">
                                    <div className="row center-xs">
                                        <div className="searchPatient__formItem">
                                            <TextField
                                                theme="light"
                                                field={formik.getFieldProps("id")}
                                                label={t("patient.patientID")}
                                                isValid={isValid("id")}
                                                errorText={getErrorText("id")}
                                                onBlur={formik.handleBlur}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div className="searchPatient__buttonContainer">
                                    <Button
                                        className="searchPatient__button"
                                        type="submit"
                                        disabled={searchStatus === "LOADING"}
                                    >
                                        <SearchIcon width="20" height="20" />
                                        <div className="searchPatient__button__label">
                                            {t("common.search")}
                                        </div>
                                    </Button>
                                </div>
                                <div className="searchPatient__secondary">
                                    <div className="searchPatient__info">
                                        {t("common.searchpatientinstruction")}
                                    </div>
                                    <div className="row center-xs">
                                        <div className="searchPatient__formItem">
                                            <TextField
                                                field={formik.getFieldProps("firstName")}
                                                theme="regular"
                                                label={t("patient.firstname")}
                                                isValid={isValid("firstName")}
                                                errorText={getErrorText("firstName")}
                                                onBlur={formik.handleBlur}
                                                disabled={isSearchById}
                                            />
                                        </div>
                                        <div className="searchPatient__formItem">
                                            <TextField
                                                field={formik.getFieldProps("secondName")}
                                                theme="regular"
                                                label={t("patient.secondname")}
                                                isValid={isValid("secondName")}
                                                errorText={getErrorText("secondName")}
                                                onBlur={formik.handleBlur}
                                                disabled={isSearchById}
                                            />
                                        </div>
                                    </div>
                                    <div className="row center-xs">
                                        <div className="searchPatient__formItem">
                                            <TextField
                                                field={formik.getFieldProps("birthDate")}
                                                theme="regular"
                                                label={t("patient.birthdate")}
                                                isValid={isValid("birthDate")}
                                                errorText={getErrorText("birthDate")}
                                                onBlur={formik.handleBlur}
                                                disabled={isSearchById}
                                            />
                                        </div>
                                        <div className="searchPatient__formItem">
                                            <TextField
                                                field={formik.getFieldProps("address")}
                                                theme="regular"
                                                label={t("patient.address")}
                                                isValid={isValid("address")}
                                                errorText={getErrorText("address")}
                                                onBlur={formik.handleBlur}
                                                disabled={isSearchById}
                                            />
                                        </div>
                                    </div>
                                </div>
                            </form>
                        </div>
                        <div ref={resultsRef}>{renderSearchResults()}</div>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}


const mapStateToProps = (state: IState): IStateProps => ({
    userCredentials: state.main.authentication.data,
    patientSearchResults: state.patients.searchResults.data,
    isLoading: state.bills.createBill.status === "LOADING",
    hasSucceeded: state.bills.createBill.status === "SUCCESS",
    hasFailed: state.bills.createBill.status === "FAIL",
    searchStatus: state.patients.searchResults.status || "IDLE",
});




const mapDispatchToProps: IDispatchProps = {
    createBill,
    createBillReset,
    getPatientSuccess,
};

export default connect(mapStateToProps, mapDispatchToProps)(NewBillActivity);
