import produce from "immer";
import { IAction } from "../types";
import {
    CREATE_BILL_FAIL,
    CREATE_BILL_LOADING,
    CREATE_BILL_RESET,
    CREATE_BILL_SUCCESS,
    GET_BILL_FAIL,
    GET_BILL_LOADING,
    GET_BILL_SUCCESS,
    SEARCH_BILL_FAIL,
    SEARCH_BILL_LOADING,
    SEARCH_BILL_SUCCESS,
    UPDATE_BILL_LOADING,
    UPDATE_BILL_FAIL,
    UPDATE_BILL_RESET,
    UPDATE_BILL_SUCCESS,
} from "./consts";
import { initial } from "./initial";
import { IBillsState } from "./types";

export default produce((draft: IBillsState, action: IAction<any, any>) => {
    switch (action.type) {

        /**
         * CREATE_BILL
         */
        case CREATE_BILL_LOADING: {
            draft.createBill.status = "LOADING";
            break;
        }

        case CREATE_BILL_SUCCESS: {
            draft.createBill.status = "SUCCESS";
            delete draft.createBill.error;
            break;
        }

        case CREATE_BILL_FAIL: {
            draft.createBill.status = "FAIL";
            draft.createBill.error = action.error;
            break;
        }

        case CREATE_BILL_RESET: {
            draft.createBill.status = "IDLE";
            delete draft.createBill.error;
            break;
        }

        /**
         * SEARCH_BILL
         */
        case SEARCH_BILL_LOADING: {
            draft.searchResults.status = "LOADING";
            break;
        }

        case SEARCH_BILL_SUCCESS: {
            if (action.payload.length > 0) {
                draft.searchResults.status = "SUCCESS";
            } else {
                draft.searchResults.status = "SUCCESS_EMPTY";
            }
            draft.searchResults.data = action.payload;
            delete draft.searchResults.error;
            break;
        }

        case SEARCH_BILL_FAIL: {
            draft.searchResults.status = "FAIL";
            draft.searchResults.error = action.error;
            break;
        }

        /**
         * GET_BILL
         */
        case GET_BILL_LOADING: {
            draft.selectedBill.status = "LOADING";
            break;
        }

        case GET_BILL_SUCCESS: {
            draft.selectedBill.status = "SUCCESS";
            draft.selectedBill.data = action.payload;
            delete draft.selectedBill.error;
            break;
        }

        case GET_BILL_FAIL: {
            draft.selectedBill.status = "FAIL";
            break;
        }

        /**
         * UPDATE_BILL
         */
        case UPDATE_BILL_LOADING: {
            draft.updateBill.status = "LOADING";
            break;
        }

        case UPDATE_BILL_SUCCESS: {
            draft.updateBill.status = "SUCCESS";
            delete draft.updateBill.error;
            break;
        }

        case UPDATE_BILL_FAIL: {
            draft.updateBill.status = "FAIL";
            break;
        }

        case UPDATE_BILL_RESET: {
            draft.updateBill.status = "IDLE";
            delete draft.updateBill.error;
            break;
        }
    }
}, initial);
